mapper.update:
update sys_user_token
set user_id = #{userId,jdbcType=BIGINT},
  token = #{token,jdbcType=VARCHAR},
  modify_time = #{modifyTime,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, user_id, token, modify_time
from sys_user_token
where id = #{id,jdbcType=BIGINT}

